import { Text as PDFText, View } from '@react-pdf/renderer';
import type { Style } from '@react-pdf/types';
import type React from 'react';
import { usePdfxTheme, useSafeMemo } from '../lib/pdfx-theme-context';
import { createListStyles } from './pdfx-list.styles';
import type { ListItem, ListVariant, PdfListProps } from './pdfx-list.types';

// ─── Gap helper ──────────────────────────────────────────────────────────────

function getGapStyle(gap: 'xs' | 'sm' | 'md', styles: ReturnType<typeof createListStyles>): Style {
  if (gap === 'xs') return styles.itemRowGapXs;
  if (gap === 'md') return styles.itemRowGapMd;
  return styles.itemRowGapSm;
}

// ─── Render helpers (plain functions, not React components) ───────────────────

function renderItem(
  item: ListItem,
  index: number,
  total: number,
  variant: ListVariant,
  gap: 'xs' | 'sm' | 'md',
  styles: ReturnType<typeof createListStyles>,
  level: number
): React.ReactElement | null {
  const gapStyle = getGapStyle(gap, styles);
  const isLast = index === total - 1;
  const rowStyles: Style[] = [styles.itemRow];
  if (!isLast) rowStyles.push(gapStyle);

  if (variant === 'bullet') {
    return (
      <View key={index}>
        <View style={rowStyles}>
          {level === 0 ? (
            <View style={styles.markerBulletWrap}>
              <View style={styles.markerBulletDot} />
            </View>
          ) : (
            <View style={styles.markerBulletSubWrap}>
              <View style={styles.markerBulletSubDot} />
            </View>
          )}
          <View style={{ flex: 1 }}>
            <PDFText style={styles.itemText}>{item.text}</PDFText>
          </View>
        </View>
        {item.children && item.children.length > 0
          ? renderItemList(item.children, variant, gap, styles, level + 1)
          : null}
      </View>
    );
  }

  if (variant === 'numbered') {
    return (
      <View key={index} style={rowStyles}>
        <View style={styles.markerNumberBadge}>
          <PDFText style={styles.markerNumberText}>{`${index + 1}`}</PDFText>
        </View>
        <PDFText style={styles.itemText}>{item.text}</PDFText>
      </View>
    );
  }

  if (variant === 'checklist') {
    const isChecked = item.checked ?? true;
    return (
      <View key={index} style={rowStyles}>
        <View style={[styles.checkBox, isChecked ? styles.checkBoxChecked : {}]}>
          {isChecked ? <PDFText style={styles.checkMark}>✓</PDFText> : null}
        </View>
        <PDFText style={styles.itemText}>{item.text}</PDFText>
      </View>
    );
  }

  if (variant === 'icon') {
    return (
      <View key={index} style={rowStyles}>
        <View style={styles.iconBox}>
          <PDFText style={styles.iconMark}>★</PDFText>
        </View>
        <PDFText style={styles.itemText}>{item.text}</PDFText>
      </View>
    );
  }

  if (variant === 'multi-level') {
    return (
      <View key={index}>
        <View style={rowStyles}>
          {level === 0 ? (
            <View style={styles.markerBulletWrap}>
              <View style={styles.markerBulletDot} />
            </View>
          ) : (
            <View style={styles.markerBulletSubWrap}>
              <View style={styles.markerBulletSubDot} />
            </View>
          )}
          <PDFText
            style={[
              level === 0 ? styles.itemText : styles.itemTextSub,
              level === 0 ? styles.itemTextBold : {},
            ]}
          >
            {item.text}
          </PDFText>
        </View>
        {item.children && item.children.length > 0
          ? renderItemList(item.children, variant, gap, styles, level + 1)
          : null}
      </View>
    );
  }

  if (variant === 'descriptive') {
    return (
      <View key={index} style={rowStyles}>
        <View style={styles.descriptiveAccent} />
        <View style={styles.descriptiveContent}>
          <PDFText style={styles.descriptiveTitle}>{item.text}</PDFText>
          {item.description ? (
            <PDFText style={styles.descriptiveDesc}>{item.description}</PDFText>
          ) : null}
        </View>
      </View>
    );
  }

  return null;
}

function renderItemList(
  items: ListItem[],
  variant: ListVariant,
  gap: 'xs' | 'sm' | 'md',
  styles: ReturnType<typeof createListStyles>,
  level: number
): React.ReactElement {
  return (
    <View style={level > 0 ? styles.childrenContainer : undefined}>
      {items.map((item, index) =>
        renderItem(item, index, items.length, variant, gap, styles, level)
      )}
    </View>
  );
}

// ─── PdfList ──────────────────────────────────────────────────────────────────

export function PdfList({
  items,
  variant = 'bullet',
  gap = 'sm',
  style,
  noWrap = false,
  _level = 0,
}: PdfListProps) {
  const theme = usePdfxTheme();
  const styles = useSafeMemo(() => createListStyles(theme), [theme]);

  const containerStyles: Style[] = [styles.container];
  if (_level > 0) containerStyles.push(styles.childrenContainer);
  const styleArray = style ? [...containerStyles, style] : containerStyles;

  return (
    <View wrap={!noWrap} style={styleArray}>
      {items.map((item, index) =>
        renderItem(item, index, items.length, variant, gap, styles, _level)
      )}
    </View>
  );
}

import { StyleSheet } from '@react-pdf/renderer';
import { usePdfxTheme } from '../lib/pdfx-theme-context';
type PdfxTheme = ReturnType<typeof usePdfxTheme>;

/**
 * Creates all list styles derived from the active theme.
 * Returns a StyleSheet covering container, item row, gap variations, markers,
 * checklist/icon boxes, text styles, and nested children layout.
 * @param t - The resolved PdfxTheme instance.
 */
export function createListStyles(t: PdfxTheme) {
  const { spacing, fontWeights, typography } = t.primitives;

  return StyleSheet.create({
    container: {
      display: 'flex',
      flexDirection: 'column',
      width: '100%',
      marginBottom: t.spacing.componentGap,
    },
    itemRow: {
      flexDirection: 'row',
      alignItems: 'flex-start',
    },
    itemRowGapXs: { marginBottom: spacing[1] },
    itemRowGapSm: { marginBottom: spacing[2] },
    itemRowGapMd: { marginBottom: spacing[3] },
    // ── Bullet markers ────────────────────────────────────────────────
    markerBulletWrap: {
      width: spacing[4],
      paddingTop: 4,
      alignItems: 'center',
    },
    markerBulletDot: {
      width: 5,
      height: 5,
      borderRadius: 3,
      backgroundColor: t.colors.primary,
    },
    markerBulletSubWrap: {
      width: spacing[4],
      paddingTop: 5,
      alignItems: 'center',
    },
    markerBulletSubDot: {
      width: 4,
      height: 4,
      borderRadius: 2,
      borderWidth: 1,
      borderStyle: 'solid',
      borderColor: t.colors.mutedForeground,
      backgroundColor: 'transparent',
    },
    // ── Numbered marker ───────────────────────────────────────────────
    markerNumberBadge: {
      width: spacing[5],
      height: spacing[5],
      borderRadius: spacing[5],
      backgroundColor: t.colors.primary,
      alignItems: 'center',
      justifyContent: 'center',
      marginTop: 1,
      marginRight: spacing[2],
    },
    markerNumberText: {
      fontFamily: t.typography.body.fontFamily,
      fontSize: typography.xs,
      color: t.colors.primaryForeground,
      fontWeight: fontWeights.bold,
    },
    // ── Checklist ─────────────────────────────────────────────────────
    checkBox: {
      width: spacing[4],
      height: spacing[4],
      borderWidth: 1.5,
      borderColor: t.colors.border,
      borderStyle: 'solid',
      borderRadius: 3,
      marginTop: 1,
      marginRight: spacing[2],
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: t.colors.background,
    },
    checkBoxChecked: {
      backgroundColor: t.colors.success,
      borderColor: t.colors.success,
    },
    checkMark: {
      fontFamily: t.typography.body.fontFamily,
      fontSize: 8,
      color: t.colors.background,
      fontWeight: fontWeights.bold,
    },
    // ── Icon box ──────────────────────────────────────────────────────
    iconBox: {
      width: spacing[5],
      height: spacing[5],
      borderRadius: 4,
      backgroundColor: t.colors.primary,
      alignItems: 'center',
      justifyContent: 'center',
      marginTop: 1,
      marginRight: spacing[2],
    },
    iconMark: {
      fontFamily: t.typography.body.fontFamily,
      fontSize: 9,
      color: t.colors.primaryForeground,
      fontWeight: fontWeights.bold,
    },
    // ── Text styles ───────────────────────────────────────────────────
    itemText: {
      flex: 1,
      fontFamily: t.typography.body.fontFamily,
      fontSize: t.typography.body.fontSize,
      lineHeight: t.typography.body.lineHeight,
      color: t.colors.foreground,
    },
    itemTextSub: {
      flex: 1,
      fontFamily: t.typography.body.fontFamily,
      fontSize: t.typography.body.fontSize - 0.5,
      lineHeight: t.typography.body.lineHeight,
      color: t.colors.mutedForeground,
    },
    itemTextBold: {
      fontWeight: fontWeights.semibold,
    },
    descriptiveTitle: {
      fontFamily: t.typography.body.fontFamily,
      fontSize: t.typography.body.fontSize,
      lineHeight: t.typography.body.lineHeight,
      color: t.colors.foreground,
      fontWeight: fontWeights.semibold,
    },
    descriptiveDesc: {
      fontFamily: t.typography.body.fontFamily,
      fontSize: typography.sm,
      lineHeight: t.typography.body.lineHeight,
      color: t.colors.mutedForeground,
      marginTop: 1,
    },
    descriptiveAccent: {
      width: 3,
      borderRadius: 2,
      backgroundColor: t.colors.primary,
      marginRight: spacing[3],
      minHeight: 16,
    },
    descriptiveContent: {
      flex: 1,
    },
    childrenContainer: {
      marginLeft: spacing[5],
      marginTop: spacing[1],
    },
  });
}

import type { Style } from '@react-pdf/types';

/** List visual style variant. */
export type ListVariant =
  | 'bullet'
  | 'numbered'
  | 'checklist'
  | 'icon'
  | 'multi-level'
  | 'descriptive';

/** A single list item, optionally with nested children. */
export interface ListItem {
  /** Primary text / title of the item. */
  text: string;
  /** Optional description shown below the title (used by descriptive variant). */
  description?: string;
  /** Optional checked state (used by checklist variant). */
  checked?: boolean;
  /** Optional nested children (used by multi-level variant). */
  children?: ListItem[];
}

export interface PdfListProps {
  /** Array of list items. */
  items: ListItem[];
  /** Visual style of the list. @default 'bullet' */
  variant?: ListVariant;
  /** Spacing between list items. @default 'sm' */
  gap?: 'xs' | 'sm' | 'md';
  /** Custom style override applied to the outer container. */
  style?: Style;
  /** Indent level for nested rendering (internal use). */
  _level?: number;
  /**
   * Prevent the List from being split across PDF pages.
   * Useful for short lists that should always stay together on a single page.
   * @default false
   */
  noWrap?: boolean;
}
